mapper.select:
select count(member_id)
from basket
where member_id = #{param2}
  and items_id = #{param1};

mapper.update:
update basket
set amount=amount + #{param2}
where member_id = #{param3}
  and items_id = #{param1};

update basket
set amount=#{param3}
where member_id=#{param1} and items_id=#{param2};
