mapper.select:
select member_id
from member
where provider = #{param1}
  and email = #{param2}

select name
from member
where member_id=#{param1};

select count(email)
from member
where email=#{param1};

select phone
from member
where member_id=#{param1}

select zipno
from member
where member_id=#{param1}

select address1
from member
where member_id=#{param1}

select address2
from member
where member_id=#{param1}

select address3
from member
where member_id=#{param1}

select *
from `order`
where member_id=#{param1}
order by date desc;

select password
from member
where member_id=#{param1};

mapper.update:
update member
set name=#{name}, password=#{password}, zipno=#{zipno}, address1=#{address1}, address2=#{address2}, address3=#{address3}, phone=#{phone}
where member_id=#{member_id}
