mapper.select:
select member_id
from member
where provider = #{param1}
  and email = #{param2}

select name
from member
where member_id=#{param1};

select count(email)
from member
where email=#{param1};

select phone
from member
where member_id=#{param1}

select zipno
from member
where member_id=#{param1}

select address1
from member
where member_id=#{param1}

select address2
from member
where member_id=#{param1}

select address3
from member
where member_id=#{param1}
